mapper.select:
SELECT * FROM orders

     ( order_status = '0' OR order_status = '1' )
    AND type_id = #{con.typeId}
    AND lodger_name LIKE CONCAT('%',#{con.lodgerName},'%')
    AND lodger_phone LIKE CONCAT('%',#{con.lodgerPhone},'%')
    AND order_status = #{con.orderStatus}
    AND user_id = '0000'
    AND user_id != '0000'

        AND order_creat_time <= #{con.endTime} AND order_creat_time > #{con.beginTime}

mapper.update:
UPDATE orders SET type_id = #{order.typeId} , reserve_number = #{order.reserveNumber} ,
room_price = #{order.roomPrice} , order_price = #{order.orderPrice} , order_reality_price = #{order.orderRealityPrice} ,
order_status = #{order.orderStatus} , order_update_time = #{order.orderUpdateTime}
WHERE order_id = #{order.orderId}

UPDATE orders SET
order_status = #{status}, order_update_time = #{updateTime}
WHERE order_id = #{orderId}
