mapper.select:
SELECT * FROM room

    type_id = #{con.typeId}
    AND room_number LIKE CONCAT('%',#{con.roomNumber},'%') 
    AND room_status = #{con.roomStatus}

mapper.update:
UPDATE room SET
type_id = #{room.typeId}, room_number = #{room.roomNumber}, room_update_time = #{room.roomUpdateTime}
WHERE room_id = #{room.roomId}
