mapper.select:
SELECT r.*,rt.type_name FROM room r,room_type rt

    r.type_id = rt.type_id
    AND r.type_id = #{con.typeId}
    AND r.room_number LIKE CONCAT('%',#{con.roomNumber},'%') 
    AND r.room_status = #{con.roomStatus}

mapper.update:
UPDATE room SET
type_id = #{room.typeId}, room_number = #{room.roomNumber}, room_update_time = #{room.roomUpdateTime}
WHERE room_id = #{room.roomId}

mapper.select:
SELECT l.* FROM lodger l,room r,order_item oi
WHERE oi.item_status = '0' AND oi.room_id = r.room_id AND oi.item_id = l.order_item_id
AND oi.room_id = #{roomId}

mapper.update:
UPDATE room SET
room_status = #{status} AND room_update_time = #{updateTime}
WHERE room_id = #{roomId}
